// 3rd party library imports
//import { Translate32 } from '@carbon/icons-react';
import P5 from 'p5';
import * as Tone from 'tone';
//import { SideNav } from '../SideNav';


// project imports
import { Visualizer } from '../Visualizers';


export const matthewMadore_Visual = new Visualizer(
  'matthewMadore',
  (p5: P5, analyzer: Tone.Analyser) => {
    const width = window.innerWidth;
    const height = window.innerHeight / 2;
    // console.log('width: ' + width)
    // console.log('height: ' + height)
    //const dim = Math.min(width, height);

    const fftSize = 2048;
    analyzer.set({
      type: 'fft',
      size:  fftSize,
      smoothing: 0.5
    });

    p5.background(0, 0, 0, 255);
    p5.stroke(255, 255, 255, 255);



    // for (let i = 0; i < 220; i++) {
    //   p5.strokeWeight(dim * 0.01);
    //   p5.stroke(255 - i, 255-i, 255-i, 255);
    //   p5.noFill();
    // }


    p5.angleMode('degrees')
    const values = analyzer.getValue();




  p5.translate(width / 2 , height / 2)
  //for (let j = 0; j < 100; j++) {

      p5.beginShape();

      for (let i = 2030; i < values.length; i++) {
        const amplitude = values[i] as number;
        var r = p5.map(amplitude,(values.length - 1)*(.75), values.length - 1, 0, 20);
        var x = p5.map(i, 2040, values.length - 1, 0, width/2);
        var y = height / 2 + amplitude * height;
        x = r * Math.cos(x)
        y = r * Math.sin(y)
        // Place vertex-
        p5.vertex(x-200, y+10);
      }
      p5.endShape();
    //}
  },
);
