  keyboardKey
}: ButtonProps): JSX.Element {

  const [sampler, setSampler] = useState(
    new Tone.Sampler({
      urls: {
        A4: 'sampled_piano_a.wav',
        Bb4: 'sampled_piano_bb.wav',
        B4: 'sampled_piano_b.wav',
