function Chords_and_Notes_Generator ({
  synth,
  setSynth
}: InstrumentProps): JSX.Element {
  const setOscillator = (newType: Tone.ToneOscillatorType) => {
    setSynth(oldSynth => {
      oldSynth.disconnect()

      return new Tone.Synth({
        oscillator: { type: newType } as Tone.OmniOscillatorOptions
      }).toDestination()
    })
  }

  const oscillators: List<OscillatorType> = List([
    'sine',
    'sawtooth',
    'square',
    'triangle',
    'fmsine',
    'fmsawtooth',
    'fmtriangle',
    'amsine',
    'amsawtooth',
    'amtriangle'
  ]) as List<OscillatorType>

  return (
    <div className='pv4bg-near-black">'>
      <div
        className='pb2 pl4'
        style={{
          top: '0rem',
          left: '5rem'
        }}
      >
        Instructions
      </div>
      <div className='relative flex-wrap h4 w-100 ml4'>
        {generateKeyToPlay('A').map(c => {
          const stringOfNotes = c.notesToPlay
          return (
            <ChordOrNoteButton
              notesNameDisplay={c.notesNameDisplay} //react key
              notesToPlay={stringOfNotes}
              idx={c.idx}
              keyboardKey={c.keyboardKey}
            />
          )
        })}
      </div>
      <div className={'pl4 pt4 flex'}>
        {oscillators.map(o => (
          <WaveformType
            key={o}
            title={o}
            onClick={() => setOscillator(o)}
            //active={synth?.oscillator.type === o}
          />
        ))}
      </div>
    </div>
  )
}

