// 3rd party library imports
import * as Tone from 'tone';
import classNames from 'classnames';
import { List, Range } from 'immutable';
// import React from 'react';
import React, { useState } from 'react';

// project imports
import { Instrument, InstrumentProps } from '../Instruments';

/** ------------------------------------------------------------------------ **
 * Contains implementation of components for Piano.
 ** ------------------------------------------------------------------------ */




interface DrumKitProps {
  name: string;
  prefix: string;
  kitNum: number;
  urls: {
    A3: string; //kick
    B3: string; //snare
    C3: string; //highHat
    D3: string; //tomLeft
    E3: string; //tomRight
    Eb3: string;//floorTom
    F3: string; //crash
    G3: string; //ride
  }
  // color: string;
}

function getDrumKitProps(){
  return {
    name: 'Kurzweil Kit 01',
    prefix: 'Kurzweil_Kit_01/',
    kitNum: 0,
      urls: {
        A3: 'CYCdh_Kurz01-Kick03.wav',
        B3: 'CYCdh_Kurz01-Snr02.wav',
        C3: 'CYCdh_Kurz01-ClHat.wav',
        D3: 'CYCdh_Kurz01-Tom04.wav', //leftTom
        E3: 'CYCdh_Kurz01-Tom03.wav', //rightTom
        Eb3: 'CYCdh_Kurz01-Tom01.wav', //floorTom
        F3: 'CYCdh_Kurz01-Crash01.wav',
        G3: 'CYCdh_Kurz01-Ride01.wav'
      }
      // TODO: add floor tom as Tom01 and make leftTom and rightTom in 03,04
    } as DrumKitProps;
  };

  const urlPrefix = 'Natedude/'

  // takes in DrumKitProps and returns [sampler, setSampler]
  function MakeSampler(newKit: DrumKitProps){
    const [sampler, setSampler] = useState(
      new Tone.Sampler({
        urls: newKit.urls,
        baseUrl: `http://localhost:3000/${urlPrefix}${newKit.prefix}`,
      }).toDestination()
      );
      return [sampler, setSampler] as [Tone.Sampler, React.Dispatch<React.SetStateAction<Tone.Sampler>>];
    }

    interface DrumKitPieceProps {
      note: string; // kick, snare, highHat, tomLeft, tomRight, crash, ride
      sampler: Tone.Sampler;
      styles: Object;
      hitboxStyles: Object;
    }

    export function DrumKitPiece({
      note,
      sampler,
      styles,
      hitboxStyles
    }: DrumKitPieceProps): JSX.Element {
      /**
       * This React component corresponds to either a major or minor key in the piano.
       * See `PianoKeyWithoutJSX` for the React component without JSX.
       */
      return (
        // Observations:
        // 1. The JSX refers to the HTML-looking syntax within TypeScript.
        // 2. The JSX will be **transpiled** into the corresponding `React.createElement` library call.
        // 3. The curly braces `{` and `}` should remind you of string interpolation.
        <div
        className={classNames('b--blue ba pointer absolute h1')}
        style={Object.assign({},
          {
            // CSS
            backgroundSize: 'contain',
            backgroundRepeat: 'no-repeat',
          }, styles)}
          >
          <div
          onMouseDown={() => sampler.triggerAttack(`${note}`)}
          onMouseUp={() => sampler.triggerRelease(`${note}`,'+0.25')}
          className={classNames('b--red ba')}
          style={hitboxStyles}
          >h</div>
          {/* <div></div> */}
        </div>
      );
    }

// function PianoType({ title, onClick, active }: any): JSX.Element {
//   return (
//     <div
//       onClick={onClick}
//       className={classNames('dim pointer ph2 pv1 ba mr2 br1 fw7 bw1', {
//         'b--red green': active,
//         'gray b--light-gray': !active,
//       })}
//     >
//       {title}
//     </div>
//   );
// }
//  Object.keys(drumKits)
// interface Styles {
//   backgroundImage: string;
//   width: string;
//   height: string;
//   top: string;
//   left: string;
// }

// interface DrumKitPieceSpec {

// }

function DrumKit({ synth, setSynth }: InstrumentProps): JSX.Element {

  // drumPieces instead of keys
  const wUnit = 'rem'
  const hUnit = 'rem'

  const drumPieces = [
    {
      note: 'F3', //crash
      styles: {
        backgroundImage: 'url(Natedude/img/crash.png)',
        width: `8${wUnit}`,
        height: `20${hUnit}`,
        top: `2${hUnit}`,
        left: `3${wUnit}`,
      },
      hitboxStyles:{
        float: 'right',
        width: `6.5${wUnit}`,
        height: `4.5${hUnit}`,
        // borderRadius: '1rem',
        borderTopLeftRadius: '25%',
        borderTopRightRadius: '50%',
        borderBottomRightRadius: '50% 25%',
        borderBottomLeftRadius: '50% 25%',

      }
    },
    {
      note: 'A3', //kick
      styles: {
        backgroundImage: 'url(Natedude/img/kick.png)',
        width: `11${wUnit}`,
        height: `11${hUnit}`,
        top: `11${hUnit}`,
        left: `11${wUnit}`,
      },
      hitboxStyles:{

      }
    },
    {
      note: 'C3', //high hat
      // (putting first so it is behind snare in UI)
      styles: {
        backgroundImage: 'url(Natedude/img/highHat.png)',
        width: `7${wUnit}`,
        height: `3.5${hUnit}`,
        top: `7${hUnit}`,
        left: `2${wUnit}`,
      },
      hitboxStyles:{

      }
    },
    {
      note: 'B3', //snare
      styles: {
        backgroundImage: 'url(Natedude/img/snare.png)',
        width: `7${wUnit}`,
        height: `3${wUnit}`,
        top: `11${hUnit}`,
        left: `5${wUnit}`,
      },
      hitboxStyles:{

      }
    },

    {
      note: 'D3', //leftTom
      styles: {
        backgroundImage: 'url(Natedude/img/leftTom.png)',
        width: `6${wUnit}`,
        height: `4.5${hUnit}`,
        top: `6.5${hUnit}`,
        left: `10.2${wUnit}`,
      },
      hitboxStyles:{

      }
    },
    {
      note: 'E3', //rightTom
      styles: {
        backgroundImage: 'url(Natedude/img/rightTom.png)',
        width: `5${wUnit}`,
        height: `4.5${hUnit}`,
        top: `6.5${hUnit}`,
        left: `17${wUnit}`,
      },
      hitboxStyles:{

      }
    },
    {
      note: 'Eb3', //floorTom
      styles: {
        backgroundImage: 'url(Natedude/img/floorTom.png)',
        width: `7.3${wUnit}`,
        height: `11${hUnit}`,
        top: `11${hUnit}`,
        left: `22${wUnit}`,
      },
      hitboxStyles:{

      }
    },
  ]
  // const p = drumPieces[0];


  //setDrumKit
  const setOscillator = (newType: Tone.ToneOscillatorType) => {
    setSynth(oldSynth => {
      oldSynth.disconnect();

      return new Tone.Synth({
        oscillator: { type: newType } as Tone.OmniOscillatorOptions,
      }).toDestination();
    });
  };

  var [sampler, setSampler] = MakeSampler(getDrumKitProps());

  return (
    <div className="bg-near-black">
      <div className="relative dib w-100 ml4" style={{height: '43vh'}}>
        {drumPieces.map(p => {
          return (
            <DrumKitPiece
              note={p.note}
              sampler={sampler}
              styles={p.styles}
              hitboxStyles={p.hitboxStyles}
            />
          );
        })}
        {/* <DrumKitPiece
          note={p.note}
          sampler={sampler}
          image={p.image}
          height={p.height}
          width={p.width}
        /> */}
      </div>
      <div className={'pl4 pt4 flex'}>
        {}
      </div>
    </div>
  );
}

export const Natedude_Instrument = new Instrument('Natedude_Instrument', DrumKit);
