// 3rd party
import { List, Map } from 'immutable';

// project dependencies
import { PianoInstrument } from './instruments/Piano';
import { WaveformVisualizer } from './visualizers/Waveform';
// matthewMadore
import { matthewMadore_Instrument } from './instruments/matthewMadore_Instrument';
import { matthewMadore_Visual } from './visualizers/matthewMadore_Visual';
// Nathan Hildum - Natedude
import { Natedude_Instrument } from './instruments/Natedude_Instrument';
import { Natedude_Visual } from './visualizers/Natedude_Visual';
// Neesha Magar
import { Nishamgr_Flute } from './instruments/Nishamgr_Flute';
import { Nishamgr_Circle } from './visualizers/Nishamgr_Circle';

/** ------------------------------------------------------------------------ **
 * The entire application state is stored in AppState.
 ** ------------------------------------------------------------------------ */

/**
 * Observation: pure map (compare and contrast with impure map)
 *
 * 'instrument': Instrument
 * 'visualizer': Visualizer
 */
export type AppState = Map<string, any>;


const instruments = List([PianoInstrument, matthewMadore_Instrument, Natedude_Instrument, Nishamgr_Flute]);
const visualizers = List([WaveformVisualizer, matthewMadore_Visual, Natedude_Visual, Nishamgr_Circle]);
export const defaultState: AppState = Map<string, any>({
  instruments,
  visualizers,
});
