      'dominant': newKeyToPlay.get(11)?.notesToPlay,
      'submediant': newKeyToPlay.get(12)?.notesToPlay,
      'leadingTone': newKeyToPlay.get(13)?.notesToPlay,
      'octave': newKeyToPlay.get(13)?.notesToPlay,
    }
  } else {
    playChordOrNote = {
      'majorI': ['Ab3', 'B4', 'Eb4'],  // [A, Db, E]
      'minorii': ['Bb4', 'Db4', 'F4'],
      'minoriii': ['B4', 'Eb4', 'Gb4'],
