        break
      }
    }
    return soundToPlay
  }
  console.log(`idx:${idx}`);
  const displayString = `${keyboardKey}${notesNameDisplay}\nidx:${idx}`

  const topPlacement = (): number => {
    if (idx <= 4 || idx >= 8 && idx <= 11) {
      // left 1st row
      return 0;
    } else {
      // left 2nd row
      return 4;
    }
  }

  const leftPlacement = (): number => {
    if (idx <= 4 || idx >= 8 && idx <= 11) {
      // top left
      return (idx * 6) - (6);
    } else if (idx ) {
      // top right
      return 0
    } else {
      // bottom right
      return (idx * 6) - (6 * 4);
    }
  }
  return (
    <div
      onMouseDown={() => sampler?.triggerAttack(['A2'])} // Question: what is `onMouseDown`?
      onMouseUp={() => sampler?.triggerRelease(['A2'], '+0.10')} // Question: what is `onMouseUp`?
      // onKeyDown={(e) => sampler?.triggerAttack(`${keyHandler(e)}`)}
      onKeyDown={e => sampler?.triggerAttack(`${keyDownHandler(e)}`)}
      tabIndex={0}
      onKeyUp={e => sampler?.triggerRelease(`${keyUpHandler(e)}`, '+0.10')}
      className={classNames('ba pointer absolute dim blue yellow bg-red h1'
      )}
      style={{
        // CSS
        top: `${topPlacement()}rem`,
        left: `${leftPlacement()}rem`,
        zIndex: 0,
        height: '3rem',
        width: '5rem',
        padding: 0.1,
        marginLeft: '0.25rem'
        // `${event.key}``${}`
      }}
