// 3rd party library imports
import * as Tone from 'tone';
import classNames from 'classnames';
import { List, Range } from 'immutable';
// import React from 'react';
import React, { useState } from 'react';

// project imports
import { Instrument, InstrumentProps } from '../Instruments';

/** ------------------------------------------------------------------------ **
 * Contains implementation of components for Piano.
 ** ------------------------------------------------------------------------ */




interface DrumKitProps {
  name: string;
  prefix: string;
  kitNum: number;
  urls: {
    A3: string; // kick
    B3: string; //snare
    C3: string; //highHat
    D3: string; //tomLeft
    E3: string;
    F3: string;
    G3: string;
  }
  // color: string;
}

function getDrumKitProps(){
  return {
    name: 'Kurzweil Kit 01',
    prefix: 'Kurzweil_Kit_01/',
    kitNum: 0,
    // styles: {
      //   top: 0,
      //   left: 20rem,
      // }
      urls: {
        A3: 'CYCdh_Kurz01-Kick03.wav',
        B3: 'CYCdh_Kurz01-Snr02.wav',
        C3: 'CYCdh_Kurz01-ClHat.wav',
        D3: 'CYCdh_Kurz01-Tom01.wav',
        E3: 'CYCdh_Kurz01-Tom02.wav',
        F3: 'CYCdh_Kurz01-Crash01.wav',
        G3: 'CYCdh_Kurz01-Ride01.wav'
      }
    } as DrumKitProps;
  };
  const urlPrefix = 'Natedude/'
  // takes in DrumKitProps and returns [sampler, setSampler]
  function MakeSampler(newKit: DrumKitProps){
    const [sampler, setSampler] = useState(
      new Tone.Sampler({
        urls: newKit.urls,
        baseUrl: `http://localhost:3000/${urlPrefix}${newKit.prefix}`,
      }).toDestination()
      );
      return [sampler, setSampler] as [Tone.Sampler, React.Dispatch<React.SetStateAction<Tone.Sampler>>];
    }

    interface DrumKitPieceProps {
      note: string; // kick, snare, highHat, tomLeft, tomRight, crash, ride
      sampler: Tone.Sampler;
      // placement or styles?
      // image?: string;
      // height?: string;
      // width?: string;
      // top?: string;
      // left?: string;
      styles: Object;
    }

    // interface DrumKitPieceProps {
    //   note: string; // C, Db, D, Eb, E, F, Gb, G, Ab, A, Bb, B
    //   duration?: string;
    //   synth?: Tone.Synth; // Contains library code for making sound
    //   minor?: boolean; // True if minor key, false if major key
    //   octave: number;
    //   index: number; // octave + index together give a location for the piano key
    // }

    export function DrumKitPiece({
      note,
      sampler,
      // placement or styles
      // image,
      // height,
      // width,
      // top,
      // left
      styles
    }: DrumKitPieceProps): JSX.Element {
      /**
       * This React component corresponds to either a major or minor key in the piano.
       * See `PianoKeyWithoutJSX` for the React component without JSX.
       */

      return (
        // Observations:
        // 1. The JSX refers to the HTML-looking syntax within TypeScript.
        // 2. The JSX will be **transpiled** into the corresponding `React.createElement` library call.
        // 3. The curly braces `{` and `}` should remind you of string interpolation.
        <div
          onMouseDown={() => sampler.triggerAttack(`${note}`)}
          onMouseUp={() => sampler.triggerRelease(`${note}`,'+0.25')}
          className={classNames('pointer absolute h1')}
          style={Object.assign({},
            {
              // CSS
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat',
            }, styles)}
        ></div>
      );
    }

// function PianoType({ title, onClick, active }: any): JSX.Element {
//   return (
//     <div
//       onClick={onClick}
//       className={classNames('dim pointer ph2 pv1 ba mr2 br1 fw7 bw1', {
//         'b--red green': active,
//         'gray b--light-gray': !active,
//       })}
//     >
//       {title}
//     </div>
//   );
// }
//  Object.keys(drumKits)

function DrumKit({ synth, setSynth }: InstrumentProps): JSX.Element {

  // drumPieces instead of keys
  const drumPieces = [
    {
      note: 'A3', //kick
      styles: {
        backgroundImage: 'url(Natedude/img/kick.png)',
        width: '25vw',
        height: '25vh',
        top: '15vh',
        left: '10vw'
      }
      // styles: {
      //   top:0
      // }
    },
    {
      note: 'C3', //high hat
      styles: {
        backgroundImage: 'url(Natedude/img/snare.png)',
        width: '10vw',
        height: '4.5vw',
        top: '18vh',
        left: '2vw'
      }
    },
    {
      note: 'B3', //snare
      styles: {
        backgroundImage: 'url(Natedude/img/snare.png)',
        width: '10vw',
        height: '4.5vw',
        top: '18vh',
        left: '2vw'
      }
    },

  ]
  const p = drumPieces[0];

  //setDrumKit
  const setOscillator = (newType: Tone.ToneOscillatorType) => {
    setSynth(oldSynth => {
      oldSynth.disconnect();

      return new Tone.Synth({
        oscillator: { type: newType } as Tone.OmniOscillatorOptions,
      }).toDestination();
    });
  };

  var [sampler, setSampler] = MakeSampler(getDrumKitProps());

  return (
    <div className="bg-near-black">
      <div className="relative dib w-100 ml4" style={{height: '43vh'}}>
        {drumPieces.map(p => {
          return (
            <DrumKitPiece
              note={p.note}
              sampler={sampler}
              styles={p.styles}
            />
          );
        })}
        {/* <DrumKitPiece
          note={p.note}
          sampler={sampler}
          image={p.image}
          height={p.height}
          width={p.width}
        /> */}
      </div>
      <div className={'pl4 pt4 flex'}>
        {}
      </div>
    </div>
  );
}

export const Natedude_Instrument = new Instrument('Natedude_Instrument', DrumKit);
