// 3rd party library imports
import * as Tone from 'tone';
import classNames from 'classnames';
import { List } from 'immutable';

// project imports
import { Instrument, InstrumentProps } from '../Instruments';

/** ------------------------------------------------------------------------ **
 * Contains implementation of components for Piano.
 ** ------------------------------------------------------------------------ */

interface ToneHoleProps {
  note: string; // C, Db, D, Eb, E, F, Gb, G, Ab, A, Bb, B
  duration?: string;
  synth?: Tone.Synth; // Contains library code for making sound
  minor?: boolean; // True if minor key, false if major key
  octave?: number;
  index: number; // octave + index together give a location for the piano key
}

interface FluteStripeProps {
  color?: string; // Hex value or color name, #786663 or, red
  width?: string; // width in pixels
  height?: string; // in pixels
}

/**
 * This component is used to create the stripes on the flute
 * @param param0
 * @returns
 */
export function FluteStripe({ color, width, height }: FluteStripeProps): JSX.Element {
  return (
    // Observations:
    // 1. The JSX refers to the HTML-looking syntax within TypeScript.
    // 2. The JSX will be **transpiled** into the corresponding `React.createElement` library call.
    // 3. The curly braces `{` and `}` should remind you of string interpolation.
    <div
      style={{
        backgroundColor: `${color || 'red'}`,
        height: `${height || 70}px`,
        width: `${width || 25}px`,
      }}
    ></div>
  );
}

export function ToneHole({ note, synth, minor, index }: ToneHoleProps): JSX.Element {
  /**
   * This component will manage the tone holes
   * See `PianoKeyWithoutJSX` for the React component without JSX.
   */
  return (
    // Observations:
    // 1. The JSX refers to the HTML-looking syntax within TypeScript.
    // 2. The JSX will be **transpiled** into the corresponding `React.createElement` library call.
    // 3. The curly braces `{` and `}` should remind you of string interpolation.
    <div
      onMouseDown={() => synth?.triggerAttack(`${note}`)} // Question: what is `onMouseDown`?
      onMouseUp={() => synth?.triggerRelease('+0.35')} // Question: what is `onMouseUp`?
      className="bg-black"
      style={{
        borderRadius: '50%',
        height: '2rem',
        width: '2rem',
        marginTop: '1.3rem',
        left: `${index * 3}rem`,
      }}
    ></div>
  );
}

function FluteType({ title, onClick, active }: any): JSX.Element {
  return (
    <div
      onClick={onClick}
      className={classNames('dim pointer ph2 pv1 ba mr2 br1 fw7 bw1', {
        'b--black black': active,
        'gray b--light-gray': !active,
      })}
    >
      {title}
    </div>
  );
}

function Flute({ synth, setSynth }: InstrumentProps): JSX.Element {
  // Adjust keys, add or remove new keys
  const keys = List([
    { note: 'C3', idx: 0 },
    { note: 'C4', idx: 1 },
    { note: 'C5', idx: 2 },
    { note: 'C6', idx: 3 },
    { note: 'E3', idx: 4 },
    { note: 'E4', idx: 5 },
    { note: 'E5', idx: 6 },
    { note: 'A3', idx: 7 },
    { note: 'A4', idx: 8 },
  ]);

  const setOscillator = (newType: Tone.ToneOscillatorType) => {
    setSynth((oldSynth) => {
      oldSynth.disconnect();

      return new Tone.Synth({
        oscillator: { type: newType } as Tone.OmniOscillatorOptions,
      }).toDestination();
    });
  };

  const oscillators: List<OscillatorType> = List([
    'sine',
    'sawtooth',
    'square',
    'triangle',
    'fmsine',
    'fmsawtooth',
    'fmtriangle',
    'amsine',
    'amsawtooth',
    'amtriangle',
  ]) as List<OscillatorType>;

  return (
    <div className="pv4">
      <div className="bg-yellow flex" style={{ height: '70px', width: '570px', margin: 'auto' }}>
        <FluteStripe />

        <div className="ml2 mr2">
          <ToneHole note="A5" synth={synth} index={0} />
        </div>

        <FluteStripe color="#aa0000" />
        <FluteStripe color="#00ee00" />
        <FluteStripe color="#4444ff" />

        <div className="ml-auto flex">
          {keys.map((key, indx) => {
            const isMinor = key.note.indexOf('b') !== -1;
            const note = `${key.note}`;
            return (
              <div key={indx} className="mr2">
                <ToneHole
                  key={note} //react key
                  note={note}
                  synth={synth}
                  minor={isMinor}
                  index={key.idx + 1}
                />
              </div>
            );
          })}
        </div>
      </div>

      <div className={'pl4 pt4 flex'}>
        {oscillators.map((o) => (
          <FluteType
            key={o}
            title={o}
            onClick={() => setOscillator(o)}
            active={synth?.oscillator.type === o}
          />
        ))}
      </div>
    </div>
  );
}

export const Nishamgr_Flute = new Instrument('Nishamgr_Flute', Flute);
